async function getReactions () {
  try {
    var giveawayMsg = tempVars('giveawayMessageSent');
    var giveawayEmbed = tempVars('giveawayMessage');
    var numberOfMembers = tempVars('numberOfMembers');
    var winnerArray = [];
    var winnerText = '';
    var users = await giveawayMsg.reactions.get('ðŸŽ‰').fetchUsers()
    var userArray = users.array();

    if (userArray.length > 1) {
      userArray = userArray.filter(function(item) { 
        return item.id !== client.user.id
      })
      if (userArray.length >= numberOfMembers) {
        for (var i = 0; i < numberOfMembers; i++) {
            if (userArray[i].id != client.user.id) {
                var randomNumber = Math.floor(Math.random() * userArray.length);
                var randomuser = await userArray[randomNumber];
                winnerArray.push(randomuser);
                userArray.splice(userArray.indexOf(randomNumber), randomNumber);
            }
        }
        for (i = 0; i < winnerArray.length; i++) {
          winnerText = winnerText + winnerArray[i] + '\n'
        }
        giveawayEmbed.author.name = 'ðŸŽ‰ Giveaway Ended ðŸŽ‰';
        giveawayEmbed.description = giveawayEmbed.description + '\n \n Winners: \n' + winnerText
        giveawayEmbed.footer.text = giveawayEmbed.footer.text.replace('will end', 'ended');
        giveawayMsg.edit(giveawayEmbed)
        giveawayMsg.channel.send('ðŸŽ‰ Congratulations! ðŸŽ‰ \n' + winnerText + 'They have won **' + tempVars('giveawayObject').content + '**');
      } else {
        for (i = 0; i < userArray.length - 1; i++) {
            if (userArray[i].id != client.user.id) {
                var randomNumber = Math.floor(Math.random() * userArray.length);
                var randomuser = await userArray[randomNumber];
                winnerArray.push(randomuser)
                userArray.splice(userArray.indexOf(randomNumber) , randomNumber);
            }
        }
        for (i = 0; i < winnerArray.length; i++) {
          winnerText = winnerText + winnerArray[i] + '\n';
        }
        giveawayEmbed.author.name = 'ðŸŽ‰ Giveaway Ended ðŸŽ‰'
        giveawayEmbed.description = giveawayEmbed.description + '\n \n Winners: \n' + winnerText
        giveawayMsg.edit(giveawayEmbed)
        giveawayMsg.channel.send('ðŸŽ‰ Congratulations! ðŸŽ‰ \n' + winnerText + '\nThey have won **' + tempVars('giveawayObject').content + '**\n \nNote:The number of people who reacted isn\'t enough to satisfy the numbers of members that can win so the bot has gotten the maximum number of people it can draw to win.')
      }
    } else {
      msg.channel.send({
        embed: {
          color: 0xff6666,
          author: {
            name: 'No one reacted',
            icon_url: (client || this.getDBM().Bot.bot).user.avatarURL
          },
          description: 'None has reacted so no one won. Oh well'
        }
      })
    }
  } catch (error) {
    msg.channel.send({
      embed: {
        color: 0xff6666,
        author: {
          name: 'Error - Giveaway Error',
          icon_url: (client || this.getDBM().Bot.bot).user.avatarURL
        },
        description: 'Please retry the command, the bot has errored \n This may be due to the fact that it may of not been able to grab the reactions \n An error message has been outputted to console for futher debugging'
      }
    })
    console.log('Giveaway Command Error: ' + error)
  }
}
